import React from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'

const FullPizza = () => {
    const [pizza, setPizza] = React.useState()
    const { id } = useParams()
    console.log(pizza);

    React.useEffect(() => {
        async function fetchPizza() {
            try {
                const {data} = await axios.get('https://6298dccebf77b60258227c16.mockapi.io/items/' + id)
                console.log(data);
                setPizza(data)
            } catch (error) {
                alert('Ошибка при получении пиццы!')
            }
        }
        fetchPizza()
    },[])

    if (!pizza) {
        return 'Загрузка'
    }

  return (
    <div className="container">
        <img src={pizza.imageUrl} alt="" />
        <h2>{pizza.title}</h2>
        <h4>250 ₽</h4>
    </div>
  )
}
export default FullPizza
