import React from 'react';
import Categories from './components/Categories';
import Header from './components/Header';
import Sort from './components/Sort';
import PizzaBlock from './components/PizzaBlock';
import Skeleton from './components/PizzaBlock/Skeleton';
// import pizzas from './assets/pizzas.json'
import './scss/app.scss';
// console.log(pizzas);

function App() {
  const [items, setItems] = React.useState([])
  const [isLoading, setisLoading] = React.useState(true)

  // https://6298dccebf77b60258227c16.mockapi.io/items

  React.useEffect(() => {

    fetch('https://6298dccebf77b60258227c16.mockapi.io/items')
      .then((res) => res.json())
      .then((json) => {
        setItems(json);
        setisLoading()
      })
  }, [])
  return (
    <div className="wrapper">
      <Header />
      <div className="content">
        <div className="container">
          <div className="content__top">
            <Categories />
            <Sort />

          </div>
          <h2 className="content__title">Все пиццы</h2>
          <div className="content__items">

            {
              isLoading
                ? [...new Array(6)].map((_, index) => <Skeleton key={index} />)
                : items.map((obj) => <PizzaBlock key={obj.id} {...obj} />)
            }


            {/* {
              items.map(obj => isLoading ? <Skeleton /> : <PizzaBlock key={obj.id} {...obj} />)
            } */}

            {/* {
              pizzas.map(obj => <PizzaBlock title={obj.title} price={obj.price} imageUrl={obj.imageUrl} sizes={obj.sizes} types={obj.types}/>)
            } */}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
