        return data
    }
  )

const initialState = {
    items: [],
};

const pizzaSlice = createSlice({
    name: 'pizza',
    initialState,
    reducers: {
        setItems(state, action) {
            state.items = action.payload
        },
    },
})

export const { setItems } = pizzaSlice.actions

export default pizzaSlice.reducer
