import React from 'react'
import qs from 'qs'
import axios from 'axios'
import { useSelector, useDispatch } from 'react-redux';

import { setCategoryId, setCurrentPage, setFilters } from '../redux/slices/filterSlice'


import Categories from '../components/Categories';
import Sort from '../components/Sort';
import {list} from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlock/Skeleton';
import Pagination from '../components/Pagination';
import { SearchContext } from '../App';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate()
  const dispatch = useDispatch()
  const isSearch = React.useRef(false)
  const isMounted = React.useRef(false)


  const { categoryId, sort, currentPage } = useSelector(state => state.filter)



  const { searchValue } = React.useContext(SearchContext)
  const [items, setItems] = React.useState([])
  const [isLoading, setisLoading] = React.useState(true)

  const onChangeCategory = (id) => {
    dispatch(setCategoryId(id))
  }

  const onChangePage = (number) => {
    dispatch(setCurrentPage(number))
  }

  const fetchPizzas = async () => {
    setisLoading(true)
    const order = sort.sortProperty.includes('-') ? 'asc' : 'desc';
    const sortBy = sort.sortProperty.replace('-', '')
    const category = categoryId > 0 ? `category=${categoryId}` : ''
    const search = searchValue ? `&search=${searchValue}` : ''

    try {
      const res = await axios.get(`https://6298dccebf77b60258227c16.mockapi.io/items?page=${currentPage}&limit=4&${category}&sortBy=${sortBy}&order=${order}${search}`)
      setItems(res.data);
      setisLoading(false)
    } catch (error) {
      setisLoading(false)
      alert('')

    }

    // axios.get(`https://6298dccebf77b60258227c16.mockapi.io/items?page=${currentPage}&limit=4&${category}&sortBy=${sortBy}&order=${order}${search}`)
    //   .then((res) => {
    //     setItems(res.data);
    //     setisLoading(false)
    //   })
  }

  // Если изменили параметры и был первый рендер

  React.useEffect(() => {

    if(isMounted.current) {

      const queryString = qs.stringify({
        sortProperty: sort.sortProperty,
        categoryId,
        currentPage

      })

      navigate(`?${queryString}`)

    }

    isMounted.current = true

  }, [categoryId, sort.sortProperty, currentPage])

  // Если был первый рендер, то проверяем URL-параметры и сохраняем в редуксе
  React.useEffect(() => {
    if (window.location.search) {
      const params = qs.parse(window.location.search.substring(1))
      const sort = list.find(obj => obj.sortProperty === params.sortProperty)


      dispatch(setFilters({
        ...params, 
        sort
      }))
      isSearch.current = true
    }
  },[])

  // Если был первый рендер, то запрашиваем пиццы 
  React.useEffect(() => {
    window.scrollTo(0, 0)
    if (!isSearch.current) {
      fetchPizzas()
    }

    isSearch.current = false

  }, [categoryId, sort.sortProperty, searchValue, currentPage])



  const skeletons = [...new Array(6)].map((_, index) => <Skeleton key={index} />)

  const pizzas = items.map((obj) => <PizzaBlock key={obj.id} {...obj} />)


  return (
    <div className="container">

      <div className="content__top">
        <Categories value={categoryId} onChangeCategory={onChangeCategory} />
        <Sort />

      </div>
      <h2 className="content__title">Все пиццы</h2>
      <div className="content__items">

        {
          isLoading
            ? skeletons
            : pizzas
        }


        {/* {
          items.map(obj => isLoading ? <Skeleton /> : <PizzaBlock key={obj.id} {...obj} />)
        } */}

        {/* {
          pizzas.map(obj => <PizzaBlock title={obj.title} price={obj.price} imageUrl={obj.imageUrl} sizes={obj.sizes} types={obj.types}/>)
        } */}
      </div>
      <Pagination currentPage={currentPage} onChangePage={onChangePage} />
    </div>
  )
}
