import axios from 'axios'
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { RootState } from '../store';
import { CartItem } from './cartSlice';


export const fetchPizzas= createAsyncThunk<CartItem[], Record<string, string>>(
    'pizza/fetchPizzasStatus', async (params) => {
        const { order, sortBy, category, search, currentPage } = params;
        const {data} = await axios.get<CartItem[]>(`https://6298dccebf77b60258227c16.mockapi.io/items?page=${currentPage}&limit=4&${category}&sortBy=${sortBy}&order=${order}${search}`)


        return data;
    }
  )

type Pizza = {
    id: string;
    title: string; 
    price: number; 
    imageUrl: string; 
    sizes: number[]; 
    types: number[];
}

interface PizzaSliceState {
    items: Pizza[] ;
    status: 'loading' | 'succes'| 'error'
}

const initialState: PizzaSliceState = {
    items: [],
    status: 'loading',
};

const pizzaSlice = createSlice({
    name: 'pizza',
    initialState,
    reducers: {
        setItems(state, action) {
            console.log(state.items);
            state.items = action.payload
        },
    },
    extraReducers: (builder) => {
        builder.addCase(fetchPizzas.pending, (state, action) => {
            state.status = 'loading'
            state.items = []
          });
        builder.addCase(fetchPizzas.fulfilled, (state, action) => {
            state.items = action.payload
            console.log(action.payload);

            state.status = 'succes'
          });
        builder.addCase(fetchPizzas.rejected, (state, action) => {
            state.status = 'loading'
            state.items = []
          });
    }
    // extraReducers: {
    //     [fetchPizzas.pending]: (state) => {
    //         state.status = 'loading'
    //         state.items = []

    //     },
    //     [fetchPizzas.fulfilled]: (state, action) => {
    //         state.items = action.payload
    //         console.log(action.payload);

    //         state.status = 'succes'

    //     },
    //     [fetchPizzas.rejected]: (state, action) => {
    //         state.status = 'error'
    //         state.items = []

    //     }
    // },
})

export const selectPizzaData = (state: RootState) => state.pizza

export const { setItems } = pizzaSlice.actions

export default pizzaSlice.reducer
