import React from 'react'
import { useSelector, useDispatch } from 'react-redux';

import { setCategoryId } from '../redux/slices/filterSlice'


import Categories from '../components/Categories';
import Sort from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';
import Skeleton from '../components/PizzaBlock/Skeleton';
import Pagination from '../components/Pagination';
import { SearchContext } from '../App';

export default function Home() {

  const dispatch = useDispatch()
  const categoryId = useSelector(state => state.filter.categoryId)



  const {searchValue} = React.useContext(SearchContext)

  const [items, setItems] = React.useState([])
  const [isLoading, setisLoading] = React.useState(true)
  // const [categoryId, setCategoryId] = React.useState(0)
  const [currentPage, setCurrentPage] = React.useState(1)
  const [sortType, setSortType] = React.useState({
    name: 'популярности',
    sortProperty: 'rating'
  })

  const onChangeCategory = (id) => {
    dispatch(setCategoryId(id))
  console.log(id);

  }


  // https://6298dccebf77b60258227c16.mockapi.io/items

  React.useEffect(() => {
    setisLoading(true)
    const order = sortType.sortProperty.includes('-') ? 'asc' : 'desc';
    const sortBy = sortType.sortProperty.replace('-', '')
    const category = categoryId > 0 ? `category=${categoryId}` : ''
    const search = searchValue ? `&search=${searchValue}` : ''
    fetch(
      `https://6298dccebf77b60258227c16.mockapi.io/items?page=${currentPage}&limit=4&${category}&sortBy=${sortBy}&order=${order}${search}`
    )
      .then((res) => res.json())
      .then((json) => {
        setItems(json);
        setisLoading(false)
      })
    window.scrollTo(0, 0)
  }, [categoryId, sortType, searchValue, currentPage])

  const skeletons = [...new Array(6)].map((_, index) => <Skeleton key={index} />)

  const pizzas = items.map((obj) => <PizzaBlock key={obj.id} {...obj} />)


  return (
    <div className="container">

      <div className="content__top">
        <Categories value={categoryId} onChangeCategory={onChangeCategory} />
        <Sort value={sortType} onChangeSort={(i) => setSortType(i)} />

      </div>
      <h2 className="content__title">Все пиццы</h2>
      <div className="content__items">

        {
          isLoading
            ? skeletons
            : pizzas
        }


        {/* {
          items.map(obj => isLoading ? <Skeleton /> : <PizzaBlock key={obj.id} {...obj} />)
        } */}

        {/* {
          pizzas.map(obj => <PizzaBlock title={obj.title} price={obj.price} imageUrl={obj.imageUrl} sizes={obj.sizes} types={obj.types}/>)
        } */}
      </div>
        <Pagination onChangePage={(number) => setCurrentPage(number)}/>
    </div>
  )
}
